mapper.select:
select * from tb_bbs where bbs_id = #{scvo.bbs_id} and answer_at = 'N'
and wr_active='0'

ORDER BY group_id DESC, sort_ordr ASC

	limit #{limit}


	LIMIT #{scvo.offset}, #{scvo.pageSize}

mapper.insert:
select (IFNULL(MAX(wr_id),0)+1) as group_id from tb_bbs
INSERT INTO tb_bbs ( bbs_id, wr_subject, wr_content, atch_file_id, mber_id, mber_nm, password, parent_id, group_id, wr_active, h_id, h_score, sort_ordr ) VALUES ( #{bbs_id}, #{wr_subject}, #{wr_content}, #{atch_file_id}, #{mber_id}, #{mber_nm}, #{password}, #{parent_id},
#{parent_id}
#{group_id}
, 0, #{h_id}, #{h_score}, #{sort_ordr} );

mapper.select:
select * from tb_bbs
where parent_id =#{wr_id}
and wr_active='0'

select * from
tb_bbs
